mapper.delete:
update  user set
status = "1"
where user_id =#{user_id}

mapper.select:
select

from user
where
     username =#{username}  
    and password =#{password} 
     and sex =#{sex} 
    and phone =#{phone} 
     and email =#{email} 
     and id_card =#{id_card} 
    and address =#{address}
